SELECT
  product_category,
  item,
  concat(
    product_category, ', ', item, ' -> ',
    concat_ws(' | ',
      collect_list(
        concat(Date, ': Actuals=', Actuals, ', Predicted=', Predicted)
      )
    )
  ) AS description
FROM POULTRY_DATA
GROUP BY product_category, item;
